pub(crate) fn u64_encoding_len(value: u64) -> usize {
    if value < 0x80 {
        1
    } else {
        let bits = 64 - value.leading_zeros();
        let encoding_bytes = (bits.next_multiple_of(8) / 8) as usize;
        1 + encoding_bytes
    }
}

fn apply_u64_encoding_to_hash(value: u64, hasher: &mut impl MiniDigest) {
    if value == 0 {
        hasher.update(&[0x80]);
    } else if value < 0x80 {
        hasher.update(&[value as u8]);
    } else {
        let bits = 64 - value.leading_zeros();
        let encoding_bytes = (bits.next_multiple_of(8) / 8) as usize;
        let length_bytes = value.to_be_bytes();
        hasher.update(&[0x80 + encoding_bytes as u8]);
        hasher.update(&length_bytes[(8 - encoding_bytes)..]);
    }
}

fn apply_list_concatenation_encoding_to_hash(length: u32, hasher: &mut impl MiniDigest) {
    if length < 56 {
        hasher.update(&[0xc0 + length as u8]);
    } else {
        let bits = 32 - length.leading_zeros();
        let encoding_bytes = (bits.next_multiple_of(8) / 8) as usize;
        let length_bytes = length.to_be_bytes();
        hasher.update(&[0xc0 + 55 + encoding_bytes as u8]);
        hasher.update(&length_bytes[(4 - encoding_bytes)..]);
    }
}

#[derive(Clone, Copy, Debug)]
pub(crate) enum EthereumTxInner<'a> {
    Legacy(LegacyTXInner<'a>, LegacySignatureData<'a>),
    LegacyWithEIP155(LegacyTXInner<'a>, LegacySignatureData<'a>),
    EIP2930(EIP2930Tx<'a>, EIP2718SignatureData<'a>),
    EIP1559(EIP1559Tx<'a>, EIP2718SignatureData<'a>),
    EIP4844(EIP4844Tx<'a>, EIP2718SignatureData<'a>),
    EIP7702(EIP7702Tx<'a>, EIP2718SignatureData<'a>),
}

impl<'a> EthereumTxInner<'a> {
    // NOTE: u32 chain ID allows to avoid handling some overflows below
    pub(crate) fn parse_and_compute_hashes(
        input: &'a [u8],
        expected_chain_id: u32,
    ) -> Result<((Self, Bytes32), Bytes32), ()> {
        let mut parser = Parser::new(input);
        if let Ok(tx_marker) = parser.try_parse_slice() {
            let tx_type: u8 = RLPParsableScalar::try_parse(tx_marker)?;
            match tx_type {
                1 => {
                    let mut hasher = crypto::sha3::Keccak256::new();
                    hasher.update(&[tx_type]);
                    let (tx, sig_data) = EIP2718PayloadParser::<EIP2930Tx<'a>>::try_parse_and_hash_for_signature_verification(parser.slice, &mut hasher)?;
                    if tx.chain_id != expected_chain_id as u64 {
                        return Err(());
                    }
                    let sig_hash = hasher.finalize_reset().into();

                    hasher.update(input);
                    let tx_hash = hasher.finalize().into();

                    Ok(((Self::EIP2930(tx, sig_data), sig_hash), tx_hash))
                }
                2 => {
                    let mut hasher = crypto::sha3::Keccak256::new();
                    hasher.update(&[tx_type]);
                    let (tx, sig_data) = EIP2718PayloadParser::<EIP1559Tx<'a>>::try_parse_and_hash_for_signature_verification(parser.slice, &mut hasher)?;
                    if tx.chain_id != expected_chain_id as u64 {
                        return Err(());
                    }
                    let sig_hash = hasher.finalize_reset().into();

                    hasher.update(input);
                    let tx_hash = hasher.finalize().into();

                    Ok(((Self::EIP1559(tx, sig_data), sig_hash), tx_hash))
                }
                3 => {
                    let mut hasher = crypto::sha3::Keccak256::new();
                    hasher.update(&[tx_type]);
                    let (tx, sig_data) = EIP2718PayloadParser::<EIP4844Tx<'a>>::try_parse_and_hash_for_signature_verification(parser.slice, &mut hasher)?;
                    if tx.chain_id != expected_chain_id as u64 {
                        return Err(());
                    }
                    let sig_hash = hasher.finalize_reset().into();

                    hasher.update(input);
                    let tx_hash = hasher.finalize().into();

                    Ok(((Self::EIP4844(tx, sig_data), sig_hash), tx_hash))
                }
                4 => {
                    let mut hasher = crypto::sha3::Keccak256::new();
                    hasher.update(&[tx_type]);
                    let (tx, sig_data) = EIP2718PayloadParser::<EIP7702Tx<'a>>::try_parse_and_hash_for_signature_verification(parser.slice, &mut hasher)?;
                    if tx.chain_id != expected_chain_id as u64 {
                        return Err(());
                    }
                    let sig_hash = hasher.finalize_reset().into();

                    hasher.update(input);
                    let tx_hash = hasher.finalize().into();

                    Ok(((Self::EIP7702(tx, sig_data), sig_hash), tx_hash))
                }

                _ => Err(()),
            }
        } else {
            // try as legacy
            let mut parser = Parser::new(input);
            let mut parser = parser.try_make_list_subparser()?;
            let start = parser.slice.as_ptr();
            let mut hasher = crypto::sha3::Keccak256::new();
            let legacy_inner: LegacyTXInner<'a> = RLPParsable::try_parse(&mut parser)?;
            let inner_slice = {
                let end = parser.slice.as_ptr();
                let signed_payload_slice = unsafe {
                    // Safety: they belong to the same allocation, slice is borrowed at the top of the function,
                    // and parser only moves forward
                    core::slice::from_ptr_range(start..end)
                };
                debug_assert!(signed_payload_slice.len() <= u32::MAX as usize);

                signed_payload_slice
            };
            // now we should peek
            let mut parser_to_peek = parser.clone();
            let v: u64 = RLPParsable::try_parse(&mut parser_to_peek)?;
            if v == 27 || v == 28 {
                // we can stop right here
                let legacy_signature: LegacySignatureData<'a> =
                    RLPParsable::try_parse(&mut parser)?;
                if parser.is_empty() == false {
                    return Err(());
                }

                apply_list_concatenation_encoding_to_hash(inner_slice.len() as u32, &mut hasher);
                hasher.update(inner_slice);
                let sig_hash = hasher.finalize_reset().into();

                hasher.update(input);
                let tx_hash = hasher.finalize().into();

                Ok((
                    (Self::Legacy(legacy_inner, legacy_signature), sig_hash),
                    tx_hash,
                ))
            } else {
                let min_v = 35u64 + ((expected_chain_id as u64) * 2);
                let legacy_signature: LegacySignatureData<'a> =
                    RLPParsable::try_parse(&mut parser)?;
                if !(legacy_signature.v == min_v || legacy_signature.v == min_v + 1) {
                    return Err(());
                }

                let chain_id = expected_chain_id as u64;

                // artificially compute hash for signature
                let chain_id_encoding_len = u64_encoding_len(chain_id);
                // also 2 more bytes 0x8080 for zeroes r/s
                apply_list_concatenation_encoding_to_hash(
                    (inner_slice.len() + chain_id_encoding_len + 2) as u32,
                    &mut hasher,
                );
                hasher.update(inner_slice);
                apply_u64_encoding_to_hash(chain_id, &mut hasher);
                hasher.update(&[0x80, 0x80]);
                let sig_hash = hasher.finalize_reset().into();

                hasher.update(input);
                let tx_hash = hasher.finalize().into();

                Ok((
                    (
                        Self::LegacyWithEIP155(legacy_inner, legacy_signature),
                        sig_hash,
                    ),
                    tx_hash,
                ))
            }
        }
    }
}

